function formatB() {
	let n = Sum_reach.data[0]["reach_sum"]
	if (n > 999999999){
		return (n / 1000000000).toFixed(2) + "B";
	}
  else if(n > 999999){
    return (n/1000000).toFixed(2) + "M";
	}
	else{
		return n;
	}
}()
}}

